public class HealingAmpuh extends AppCompatActivity {

    private TextView tvTitle;
    private Button btnStart, btnSettings;
    private static final String TAG = "HealingAmpuh";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_healing_ampuh);

        tvTitle = findViewById(R.id.tvTitle);
        btnStart = findViewById(R.id.btnStart);
        btnSettings = findViewById(R.id.btnSettings);

        tvTitle.setText("Healing Ampuh");

        btnStart.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                startActivity(new Intent(HealingAmpuh.this, HealingAmpuhActivity.class));
            }
        });

        btnSettings.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                startActivity(new Intent(HealingAmpuh.this, SettingsActivity.class));
            }
        });
    }
}

